import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends react.Component {
  constructor() {
   super();

    this.state = {
       monsters: [
        {
          name: 'Frankenstain',
          id: 'asc1'
        },
        {
          name:'Dracula',
          id: 'asc2'
        },
        {
          name:'Zombie',
          id: 'asc3'
        }
       ]
    };
  }


render() {
  return (
    <div className="App">
      {
        this.state.monsters.map(monsters => (
          <h1 key={monsters.id}> {monsters.name} </h1>
        ))}  
    </div> 
  );
  }
}

export default App;
